#region "Business Logic"
public class Widget : JiwaFinancials.Jiwa.JiwaApplication.JiwaCollectionItem<Widget>
{

	private string _Description;
	private int _Weight;
	private bool _IsDefault;	

	private System.DateTime m_LastSavedDateTime;
	public WidgetCollection WidgetCollection {
		get { return (WidgetCollection)Collection; }
	}

	public string Description {
		get { return _Description; }
		set {
			_Description = value;
			NotifyPropertyChanged("Description");
		}
	}

	public int Weight {
		get { return _Weight; }
		set {
			_Weight = value;
			NotifyPropertyChanged("Weight");
		}
	}

	public bool IsDefault {
		get { return _IsDefault; }
		set {
			_IsDefault = value;
			NotifyPropertyChanged("IsDefault");

			if ((Collection != null) && Collection.Reading == false) {
				if (value == true) {
					foreach (Widget otherWidget in Collection) {
						if (!otherWidget.Equals(this)) {
							if (otherWidget.IsDefault == true) {
								otherWidget.SetIsDefault(false);
							}
						}
					}
				} else {
					// You can't set the IsDefault to false : you must set another to true.
					_IsDefault = true;
					NotifyPropertyChanged("IsDefault");
				}
			}
		}
	}

	internal void SetIsDefault(bool NewValue)
	{
		_IsDefault = NewValue;
		NotifyPropertyChanged("IsDefault");
	}

	public override void SaveRecord()
	{
		string Sql = null;
		SqlParameter SQLParam = default(SqlParameter);

		var db = JiwaFinancials.Jiwa.JiwaApplication.Manager.Instance.Database;
		// Save lines

		if (DeleteFlag == false) {
			if (InsertFlag) {
				Sql = @"INSERT INTO Widget (RecID, Description, Weight, IsDefault, ItemNo) 
						VALUES (@RecID, @Description, @Weight, @IsDefault, @ItemNo)";
			} else {
				Sql = @"UPDATE Widget 
						SET Description = @Description, Weight = @Weight, IsDefault = @IsDefault, ItemNo = @ItemNo
						WHERE RecID = @RecID
						AND RowHash = @RowHash";
			}

			using (SqlCommand SQLCmd = new SqlCommand(Sql, db.SQLConnection, db.SQLTransaction)) {
				SQLCmd.CommandTimeout = db.DefaultCommandTimeout;
				SQLParam = new SqlParameter("@RecID", System.Data.SqlDbType.UniqueIdentifier);
				SQLParam.Value = new Guid(RecID);
				SQLCmd.Parameters.Add(SQLParam);

				SQLParam = new SqlParameter("@Description", System.Data.SqlDbType.VarChar);
				SQLParam.Value = Description;
				SQLCmd.Parameters.Add(SQLParam);

				SQLParam = new SqlParameter("@Weight", System.Data.SqlDbType.Int);
				SQLParam.Value = Weight;
				SQLCmd.Parameters.Add(SQLParam);

				SQLParam = new SqlParameter("@IsDefault", System.Data.SqlDbType.Bit);
				SQLParam.Value = IsDefault;
				SQLCmd.Parameters.Add(SQLParam);

				SQLParam = new SqlParameter("@ItemNo", System.Data.SqlDbType.Int);
				SQLParam.Value = ItemNo;
				SQLCmd.Parameters.Add(SQLParam);

				if (InsertFlag == false) {
					SQLParam = new SqlParameter("@RowHash", System.Data.SqlDbType.Timestamp);
					SQLParam.Value = RowHash;
					SQLCmd.Parameters.Add(SQLParam);
				}

				if (SQLCmd.ExecuteNonQuery() == 0) {
					throw new JiwaFinancials.Jiwa.JiwaApplication.Exceptions.ConcurrencyConflictException("unable to write to Widget table");
				}
			}
		}

		if (DeleteFlag) {
			Sql = "DELETE FROM Widget WHERE RecID = @RecID AND RowHash = @RowHash";

			using (SqlCommand SQLCmd = new SqlCommand(Sql, db.SQLConnection, db.SQLTransaction)) {
				SQLCmd.CommandTimeout = db.DefaultCommandTimeout;
				SQLParam = new SqlParameter("@RecID", System.Data.SqlDbType.Char);
				SQLParam.Value = RecID;
				SQLCmd.Parameters.Add(SQLParam);

				SQLParam = new SqlParameter("@RowHash", System.Data.SqlDbType.Timestamp);
				SQLParam.Value = RowHash;
				SQLCmd.Parameters.Add(SQLParam);

				if (SQLCmd.ExecuteNonQuery() == 0) {
					throw new JiwaFinancials.Jiwa.JiwaApplication.Exceptions.ConcurrencyConflictException("unable to delete from Widget");
				}
			}

		}
	}
}

public class WidgetCollection : JiwaFinancials.Jiwa.JiwaApplication.BusinessLogic.ListMaintenance<Widget>
{

	protected override void OnRemoving(Widget item)
	{
		if (item.IsDefault) {
			throw new Exception("Cannot remove the widget - make another the default first.");
		}
		base.OnRemoving(item);
	}

	public override void Read()
	{
		string Sql = null;
		SqlDataReader SQLReader = null;
		bool oldReading = Reading;

		try {
			Reading = true;
			Clear();

			var db = JiwaFinancials.Jiwa.JiwaApplication.Manager.Instance.Database;

			Sql = @"SELECT RecID, Description, Weight, IsDefault, ItemNo, RowHash 
					FROM Widget 
					ORDER BY ItemNo ";

			using (SqlCommand SQLCmd = new SqlCommand(Sql, db.SQLConnection, db.SQLTransaction)) {
				SQLCmd.CommandTimeout = JiwaFinancials.Jiwa.JiwaApplication.Manager.Instance.Database.DefaultCommandTimeout;

				SQLReader = SQLCmd.ExecuteReader();

				while (SQLReader.Read() == true) {
					Widget widget = new Widget();

					widget.RecID = db.Sanitise(SQLReader, "RecID").ToString();
					widget.Description = db.Sanitise(SQLReader, "Description").ToString();
					widget.Weight = int.Parse(db.Sanitise(SQLReader, "Weight").ToString());
					widget.IsDefault = (bool)db.Sanitise(SQLReader, "IsDefault");
					widget.ItemNo = int.Parse(db.Sanitise(SQLReader, "ItemNo").ToString());

					widget.RowHash = new byte[8];
                    SQLReader.GetBytes(5, 0, widget.RowHash, 0, 8); // 5 is the column number in the select query of the RowHash column

					Add(widget);
				}

				SQLReader.Close();
			}

			base.OnRead();

		} finally {
			Reading = oldReading;

			if ((SQLReader != null)) {
				SQLReader.Close();
			}
		}
	}

	public WidgetCollection() : base()
	{
		base.ClientClassName = "WidgetCollection";
		base.RecIDIsGUID = true;
	}
}


#endregion

#region "User Interface"
public class Widgets : JiwaFinancials.Jiwa.JiwaApplication.ListMaintenance.UserInterface<Widget, WidgetCollection>
{

	#region "Base class overrides"
	public override void Setup()
	{
		AddHandlers();
		SetupForm();
	}

	public override void AddHandlers()
	{
		grdLines.ButtonClicked += grdLines_ButtonClicked;
		grdLines.Change += grdLines_Change;
		grdLines.RowDragMoveCompleted += grdLines_RowDragMoveCompleted;
		base.AddHandlers();
	}

	#endregion

	#region "Setup"
	public void SetupForm()
	{
		SetupWindow();
		SetupControls();
	}

	private void SetupControls()
	{		
		SetupLinesGrid();
	}

	public void SetupLinesGrid()
	{
		JiwaFinancials.Jiwa.JiwaApplication.JiwaManageGrid.JiwaCurrencyCellType weightCellType = new JiwaFinancials.Jiwa.JiwaApplication.JiwaManageGrid.JiwaCurrencyCellType();
		weightCellType.DecimalPlaces = 0;

		grdLines.AddColumn("RecID", new FarPoint.Win.Spread.CellType.TextCellType(), "RecID", 10, false, false, true, true);
		grdLines.AddColumn("Description", new FarPoint.Win.Spread.CellType.TextCellType(), "Description", 20, false, true, true, false);
		grdLines.AddColumn("Weight", weightCellType, "Weight", 10, false, true, true, false);
		grdLines.AddColumn("Default", new FarPoint.Win.Spread.CellType.CheckBoxCellType(), "Default", 10, false, true, true, false);
		grdLines.AddColumn("Bin", new JiwaFinancials.Jiwa.JiwaApplication.JiwaManageGrid.JiwaBinButtonCellType(), "", 10, false, true, false, true);

		grdLines.SetupComplete();

		grdLines.ActiveSheet.RowHeader.ColumnCount = 1;
        grdLines.ActiveSheet.RowHeaderVisible = true;
        grdLines.AllowRowMove = true;
	}
	#endregion

	#region "Control Event Handlers"
	#region "grdLines"

	private void grdLines_ButtonClicked(object sender, FarPoint.Win.Spread.EditorNotifyEventArgs e)
	{
		FarPoint.Win.Spread.Column column = grdLines.ActiveSheet.Columns[e.Column];

		if (column.Tag.ToString() == "Bin") {
			BusinessLogic.Remove(BusinessLogic[grdLines.get_GridText("RecID", e.Row).ToString()]);
		} else if (column.Tag.ToString() == "Default") {
			if (grdLines.get_GridText("RecID", e.Row).ToString().Trim().Length > 0) {
				BusinessLogic[grdLines.get_GridText("RecID", e.Row).ToString()].IsDefault = System.Convert.ToBoolean(grdLines.get_GridText(column.Tag.ToString(), e.Row).ToString());
			}
		}
	}

	private void grdLines_Change(object sender, FarPoint.Win.Spread.ChangeEventArgs e)
	{
		string Key = null;
		string ColID = grdLines.ActiveSheet.Columns[e.Column].Tag.ToString();

		if (ColID == "Description") {
			if (e.Row == grdLines.ActiveSheet.RowCount - 1) {
				Widget widget = new Widget();
				widget.Description = grdLines.get_GridText("Description", e.Row).ToString();
				BusinessLogic.Add(widget);
			} else {
				Key = grdLines.get_GridText("RecID", e.Row).ToString();
				BusinessLogic[Key].Description = grdLines.get_GridText("Description", e.Row).ToString();
			}
		} else if (ColID == "Weight") {
			if (e.Row == grdLines.ActiveSheet.RowCount - 1) {
				Widget widget = new Widget();
				widget.Weight = System.Convert.ToInt32(grdLines.get_GridText("Weight", e.Row).ToString());
				BusinessLogic.Add(widget);
			} else {
				Key = grdLines.get_GridText("RecID", e.Row).ToString();
				BusinessLogic[Key].Weight = System.Convert.ToInt32(grdLines.get_GridText("Weight", e.Row).ToString());
			}
		}
	}

	private void grdLines_RowDragMoveCompleted(object sender, FarPoint.Win.Spread.DragMoveCompletedEventArgs e)
	{
	    for(int row = 0; row <= grdLines.ActiveSheet.RowCount - 1; row++)
		{
	        string recID = grdLines.get_GridText("RecID", row).ToString();

	        if (recID.Trim().Length > 0)
			{
	            BusinessLogic[recID].ItemNo = row + 1;
	        }
	    }
	}
	#endregion
    #endregion

	#region "Display"

	public override void DisplayLine(Widget item, int Row = -1)
	{
		if (Row == -1) {
			for (int MyLoop = 0; MyLoop <= grdLines.ActiveSheet.Rows.Count - 1; MyLoop++) {
				if (grdLines.get_GridText("RecID", MyLoop).ToString() == item.RecID) {
					Row = MyLoop;
					break;
				}
			}
		}

		if (Row != -1) {
			// copy col header tag into each cell tag.
			foreach (FarPoint.Win.Spread.Column column in grdLines.ActiveSheet.Columns) {
				grdLines.ActiveSheet.Cells[Row, column.Index].Tag = grdLines.ActiveSheet.ColumnHeader.Cells[1, column.Index].Tag;
			}

			grdLines.set_GridText("RecID", Row, item.RecID);
			grdLines.set_GridText("Description", Row, item.Description);
			grdLines.set_GridText("Weight", Row, item.Weight);
			grdLines.set_GridText("Default", Row, item.IsDefault);

			grdLines.LockColumn(false, "Default", Row);
			grdLines.LockColumn(false, "Bin", Row);
		}

	}

	#endregion

	public Widgets()
	{
		this.Text = "Widgets";
	}
}

#endregion
